updateRule:
@request.auth.employee.roles.name = "Admin" ||
(
  @request.auth.employee = id &&
  @request.body.department = department &&
  @request.body.roles = roles &&
  @request.body.designation = designation &&
  @request.body.salary_structure = salary_structure &&
  @request.body.leave_type = leave_type 
)

viewQuery:
SELECT id, name, type
FROM _collections
WHERE name NOT IN ('_auth', '_mfas', '_otps', '_externalAuths', '_authOrigins', '_superusers');
